import { findAllByDisplayValue } from "@testing-library/react";

export const initialState = {
    user: null,
    userPlaylists: [],
    spotify: null,
    otherUser: null,
    otherUserPlaylists: []
    // remove after debuging
    // token: 'BQAszGLEoRjtx8GLEjyuY51L3wVtVhqzJlp4tHj40vxjjPnlOJoVYf4CLO1ljQlmmmYLAyDtrXZEjs49idWL_oYma0lODhk2qvqHGldzMIuDyvcg7IIFbWDmQIrxnwLD1C5u2vr5u_bZjDm1QzNcg83d9DYOg'
};

const reducer = (state, action) => {
    // console.log(action);

    // Action -> type, [payload]

    switch (action.type) {
        case 'SET_TOKEN':
            return {
                ...state,
                token: action.token,
            };
        case "SET_SPOTIFY":
            return {
                ...state,
                spotify: action.spotify,
            };
        case 'SET_USER':
            return {
                ...state,
                user: action.user,
            };
        case 'SET_OTHER_USER':
            return {
                ...state,
                otherUser: action.otherUser,
            };
        case 'SET_PLAYLISTS':
            return {
                ...state,
                playlists: action.playlists,
            };
        case 'SET_OTHER_USER_PLAYLISTS':
            return {
                ...state,
                otherUserPlaylists: action.otherUserPlaylists,
            };
        default:
            return state;
    }
}

export default reducer;
